mapper.select:
select distinct rm.menu_id from jw_user_role ur 
	LEFT JOIN jw_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

SELECT * FROM
(
SELECT paging.*, ROWNUM RN
FROM (
select u.*, (select d.name from jw_dept d where d.dept_id = u.dept_id) deptName from jw_user u


		and u.username like concat(concat('%',#{username}),'%')



	${filterSql}



		order by u.${sidx} ${order}


		order by u.user_id desc


)  paging

	WHERE  ROWNUM <=  #{paging}
	)
	WHERE  RN  > #{offset}
